
import requests
import json
from core.config import get_settings

def test_groq_vision_v3():
    settings = get_settings()
    api_key = settings.vlm.api_key
    api_url = settings.vlm.api_url
    
    # 1x1 pixel JPEG base64 (white pixel)
    # 
    jpeg_b64 = ""
    
    headers = {
        "Content-Type": "application/json",
        "Authorization": f"Bearer {api_key}"
    }

    # Test with Llama 3.2 90B Vision (Known stable model)
    model = "llama-3.2-90b-vision-preview" 
    print(f"Test 3: Testing {model} with JPEG base64")
    
    payload = {
        "model": model,
        "messages": [
            {
                "role": "user",
                "content": [
                    {"type": "text", "text": "Describe this."},
                    {
                        "type": "image_url",
                        "image_url": {
                            "url": f"data:image/jpeg;base64,{jpeg_b64}"
                        }
                    }
                ]
            }
        ],
        "max_tokens": 10
    }
    
    try:
        response = requests.post(api_url, headers=headers, json=payload)
        if response.status_code == 200:
            print("✅ Success with 3.2-90b!")
            print(json.dumps(response.json(), indent=2))
        else:
            print(f"❌ Failed with 3.2-90b: {response.text}")
    except Exception as e:
        print(e)

if __name__ == "__main__":
    test_groq_vision_v3()
